from graphing_functions import barplot,box_strip_plot,box_strip_plot_import numpy as np import pandas as pd import matplotlib as mplimport matplotlib.pyplot as pltimport seaborn as snsif __name__ == '__main__':    jaba = pd.read_csv('../data/ngrams_csv/bigrams_jaba_clean.csv')    jaba_top_25 = jaba[:25]    barplot(data=jaba_top_25,            figsize=(20,12),            xlabel='Count',            title="Journal of Applied Behavior Analysis",            ylabel='Bigrams',            fpath='../figures/Jaba_barplot.png')        tba = pd.read_csv('../data/ngrams_csv/bigrams_tba_clean.csv')    tba_top_25 = tba[:25]    barplot(data=tba_top_25,            figsize=(20,12),            xlabel='Count',            title="The Behavior Analyst",            ylabel='Bigrams',            fpath='../figures/tba_barplot.png')            aovb = pd.read_csv('../data/ngrams_csv/bigrams_aovb_clean.csv')    aovb_top_25 = aovb[:25]    barplot(data=aovb_top_25,            figsize=(20,12),            xlabel='Count',            title="The Analysis of Verbal Behavior",            ylabel='Bigrams',            fpath='../figures/aovb_barplot.png')    bap = pd.read_csv('../data/ngrams_csv/bigrams_bap_clean.csv')    bap_top_25 = bap[:25]    barplot(data=bap_top_25,            figsize=(20,12),            xlabel='Count',            title="Behavior Analysis in Practice",            ylabel='Bigrams',            fpath='../figures/bap_barplot.png')        all_j = pd.read_csv('../data/ngrams_csv/all_journals_25_clean.csv')    all_j = all_j[:25]    barplot(data=all_j,            figsize=(20,12),            xlabel='Count',            title="All Behavior Analytic Publications",            ylabel='Bigrams',            fpath='../figures/all_boxplot.png')    all_text = pd.read_csv('../data/final_df_with_npp_dataframe.csv')    all_text = all_text[['file_path','years','text','journals','word_count']]    all_text = all_text.sort_values(by=['word_count'],ascending=False).reset_index(drop=True)    all_text = all_text.drop([0,1]).reset_index(drop=True)    data = []    for i in range(len(all_text)):      if all_text['years'][i] <= 1959:        data.append('1950-1959')      elif all_text['years'][i] <= 1969:        data.append('1960-1969')      elif all_text['years'][i] <= 1979:        data.append('1970-1979')      elif all_text['years'][i] <= 1989:        data.append('1980-1989')      elif all_text['years'][i] <= 1999:        data.append('1990-1999')      elif all_text['years'][i] <= 2009:        data.append('2000-2009')      elif all_text['years'][i] <= 2019:        data.append('2010-2019')      elif all_text['years'][i] <= 2029:        data.append('2020-2021')      else:        print(all_text['years'][i])    all_text['binned_years'] = data    box_strip_plot(df=all_text,                   figsize=(20,12),                   xlabel='Word Count Per Article',                   ymin=-1,                   ymax=6,                   title="",                   ylabel='Journal',                   fpath='../figures/word_count.png',                   xmin=0,                   xmax=25_000)    lst = {'JEAB':"Journal of the Experimental Analysis Of Behavior",           'AOVB':"Analysis of Verbal Behavior",           'JABA':'Journal of Applied Behavior Analysis',           'TBA_':'The Behavior Analyst',           'Cont':'Health Service Research',           'BAP_':'Behavior Analysis in Practice'}    all_text['replaced'] = all_text.journals.replace(lst)    box_strip_plot_(all_text,                   figsize=(20,12),                   xlabel='Year',                   title="",                   ylabel='Word Count',                   fpath='../figures/word_count_year.png',ymin=0,ymax=25000)                                                